import React from 'react'
import './App.css'
import script from './components/StarWars'

function App() {
  // MARK: Define text objects
  // TODO
  let words = {}
  let newWord = ''

  // MARK: Get data
  // TODO

  // MARK: Iterate over words in list and place into new object
  for(const char of script) {

    // Add string to new word until space
    if(char !== " ") {
      newWord += char.replace(/[.,\/#!$%\^&\*;:{}=\-_`~()\n 0-9]/g, "").toLowerCase()
    } else {
      if(newWord.length > 1) {
        if(words[newWord]) {
          words[newWord] += 1
        } else {
          words[newWord] = 1
        }
        newWord = ''
      }
    }
  }

  // Order words
  let sortedWords = Object.keys(words).sort(
    function(a, b) {
      return words[b] - words[a]
  })
  console.log(sortedWords)

  let shortList = []
  for(let i = 0; i < 10; i++) {
    shortList += sortedWords[i]
  }

  return (
    <div>
      <h1>Hello world!</h1>
      <p>{shortList}</p>
    </div>
  )
}

export default App
