import React from 'react'
import './App.css'
import script from './components/StarWars'
import { Button, Jumbotron } from 'react-bootstrap'

function App() {
  // MARK: Define text objects
  let words = {}
  let newWord = ''

  // MARK: Get data

  // MARK: Iterate over words in list and place into new object
  for(const char of script) {
    if(char !== " ") {
      newWord += char.replace(/[.,\/#!$%\^&\*;:{}=\-_`~()\n 0-9]/g, "").toLowerCase()
    } else {
      if(newWord.length > 1) {
        if(words[newWord]) {
          words[newWord] += 1
        } else {
          words[newWord] = 1
        }
        newWord = ''
      }
    }
  }

  // MARK: Order words
  let sortedWords = Object.keys(words).sort(
    function(a, b) {
      return words[b] - words[a]
  })
  console.log(sortedWords)

  let items = []
  for(let i = 0; i < 20; i++) {
    items.push(<p><Button className="ml-1 mr-1" variant="primary">{sortedWords[i]}</Button></p>)
  }

  return (
    <div className="d-inline-flex align-items-center align-self-center">
      <Jumbotron className="d-inline-flex w-75 flex-wrap">
        <h1>Most Used Words in:</h1>
        <h2>Star Wars</h2>
        {items}
      </Jumbotron>
    </div>
  )
}

export default App
